mapper.insert:
INSERT
	INTO mem_info
	VALUES (
		null 
		, #{mem_id} 
		, #{mem_passwd} 
		, #{mem_name}  
		, CONCAT(#{phone1}, '-', #{phone2}, '-', #{phone3})  
		, CONCAT(#{sample6_address},' ',#{sample6_detailAddress},' ',#{sample6_extraAddress})  
		, #{mem_birthday}  
		, now()  
	)

mapper.select:
SELECT mem_id
	FROM mem_info
	WHERE mem_name = #{mem_name} AND mem_mtel = #{mem_mtel}
